package assignment5;

public class Question7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0,j=0;
		int count=1;
		for(i=1;i<=4;i++)
		{
			
			for(j=1;j<=i;j++)
			{
				System.out.printf("%d ",count);
				count++;
			}
			System.out.printf("\n");
		}
	}

}
